mapper.select:
select 

from tb_project
where id = #{id,jdbcType=INTEGER}

select p.id,p.project_code projectCode,p.branch_group_id,p.branch_group_name,p.project_type,p.transport_type,p. cargo_id,p.cargo_name,p.cargo_specifications,p.cargo_price,p.valuation_unit_name,p.send_cargo_company_id,p. send_cargo_company_name,p.receive_cargo_company_id,p.receive_cargo_company_name,p.send_cargo_unit_id,p. send_cargo_unit_name,p.receive_cargo_site_id,p.receive_cargo_site,p.receive_center_cargo_site_id,p. receive_cargo_site_freight_yard_id,p.short_barge_carrier_mode,p.short_barge_carrier_id,p. short_barge_carrier_name,p.send_short_barge_carrier_mode,p.begin_center_site_id,p.begin_center_site_name,p. begin_site_id,p.begin_site_name,p.begin_site_freight_yard,p.begin_address,p.end_center_site_id,p. end_center_site_name,p.end_site_id,p.end_site_name,p.end_site_freight_yard,p.end_address,p. freight,p.material_cost,p.tarpaulin_cost,p.begin_stevedoring_cost,p.end_stevedoring_cost,p. freight_sum,p.forwarding_site_id,p.forwarding_site_name,p.forwarding_center_site_id,p. forwarding_site_freight_yard_id,p.forwarding_unit_id,p.forwarding_unit_name,p.take_place,p. take_place_detail,p.receiving_department_id,p.receiving_department_name,p.arrive_place,p. arrive_place_address,p.pick_up_price,p.train_price,p.arrive_price,p.transport_price,p. remark,p.create_date,p.project_distribution_id,p.edit_date,p.status,p.creator_id,p.principal,p. is__distribution,p.delete_flag,p.finish_date
from tb_project p left join tb_branch_group_link y on p.id=y.row_id where branch_group_id in
#{item}
and y.tab_name='tb_project' and y.sys_org_code like CONCAT(#{sysOrgCode},'%') and delete_flag = 0 and status in (1,3)
and transport_type in (1,2,4,5,6,7) and project_type = #{projectType,jdbcType=TINYINT}
and transport_type in (0,1,2,4,5,6,7) and project_type = #{projectType,jdbcType=TINYINT}

select from tb_project where branch_group_id in
#{item}
and delete_flag = 0

SELECT A.id driverId,B.id informationId,A.car_team_id teamId,B. NAME ownerCarName,B.model model,B.driver_name driverName, B.brand brand,B.motorcycle_type motorcycleType,B.driver_phone driverPhone,B.plate_number plateNumber,C.transport_price transportPrice, C.deduction_rate deductionRate,C.deduction_price deductionPrice FROM tb_login_user AS A, tb_user_information AS B, tb_short_barge AS C WHERE C.short_barge_id = A.car_team_id AND A.car_team_id = C.short_barge_id AND c.short_barge_type = 1 AND A.information_id = B.id AND A.type = 0 AND A.state = 1 AND A.order_status = 0
and C.project_id = #{projectId,jdbcType = INTEGER}
and A.id = #{driverId,jdbcType = INTEGER}
AND c.type = #{shortType,jdbcType = INTEGER}

SELECT A.id driverId, B.id informationId, A.car_team_id teamId, B. NAME ownerCarName, B.model model, B.driver_name driverName, B.brand brand, B.motorcycle_type motorcycleType, B.driver_phone driverPhone, B.plate_number plateNumber, tmp.transport_price transportPrice, tmp.deduction_rate deductionRate, tmp.deduction_price deductionPrice FROM tb_login_user AS A, tb_user_information AS B, ( SELECT u.car_team_id, b.transport_price, b.deduction_rate, b.deduction_price FROM tb_user_anchored_company uac, tb_login_user u, tb_short_barge b WHERE uac.anchored_company_id = b.short_barge_id AND uac.user_id = u.id AND uac.type = 1 AND uac.is_stop = 0
AND b.project_id = #{projectId,jdbcType = INTEGER}
AND b.type = #{shortType,jdbcType = INTEGER}
AND u.state = 1 ) AS tmp WHERE tmp.car_team_id = a.car_team_id AND a.state = 1 AND a.type = 0 AND a.information_id = b.id AND a.order_status = 0
and A.id = #{driverId,jdbcType = INTEGER}
UNION ALL SELECT A.id driverId, B.id informationId, A.car_team_id teamId, B. NAME ownerCarName, B.model model, B.driver_name driverName, B.brand brand, B.motorcycle_type motorcycleType, B.driver_phone driverPhone, B.plate_number plateNumber, C.transport_price transportPrice, C.deduction_rate deductionRate, C.deduction_price deductionPrice FROM tb_user_anchored_company AS uac, tb_login_user AS A, tb_user_information AS B, tb_short_barge AS C WHERE uac.anchored_company_id = C.short_barge_id AND uac.user_id = A.id AND uac.type = 0 AND uac.is_stop = 0 AND A.state = 1 AND A.information_id = b.id AND A.order_status = 0
AND C.project_id = #{projectId,jdbcType = INTEGER}
AND C.type = #{shortType,jdbcType = INTEGER}
and A.id = #{driverId,jdbcType = INTEGER}
